{"type":"doc","content":[{"type":"text","text":"




                                        "},{"type":"paragraph","content":[{"type":"text","text":"Lemma:","marks":[{"type":"bold"}]},{"type":"text","text":" "},{"type":"katex","content":[{"type":"text","text":" \forall a \in \mathbb{R}\, a \times 0 = 0"}],"attrs":{"inline":true}}]},{"type":"text","text":"

"},{"type":"paragraph","content":[{"type":"text","text":"Proof:","marks":[{"type":"bold"}]}]},{"type":"text","text":"

"},{"type":"paragraph","content":[{"type":"katex","content":[{"type":"text","text":"
\begin{equation*}
\begin{aligned}
0a &=  0a + 0      && \quad \text{by }\textit{identity element }(+    ) \\
   &=  0a + 0a + (-0a) && \quad \text{by }\textit{inverse element  }(+    ) \\
   &=  (0 + 0)a + (-0a) && \quad \text{by }\textit{distributivity   }        \\
   &= 0a + (-0a) && \quad \text{by }\textit{identity element }(+    ) \\
   &=  0            && \quad \text{by }\textit{inverse element  }(+    )
\end{aligned}
\end{equation*}
"}],"attrs":{"inline":true}}]},{"type":"text","text":"

"},{"type":"paragraph","content":[{"type":"text","text":"Corollary:","marks":[{"type":"bold"}]},{"type":"text","text":" "},{"type":"katex","content":[{"type":"text","text":" 0 \times 0 = 0 "}],"attrs":{"inline":true}}]},{"type":"text","text":"



                            "}]}
