                                        "},{"type":"paragraph","content":[{"type":"text","text":"Suppose that a number "},{"type":"katex","content":[{"type":"text","text":"n"}],"attrs":{"inline":true}},{"type":"text","text":" has final digit "},{"type":"katex","content":[{"type":"text","text":"b"}],"attrs":{"inline":true}},{"type":"text","text":". Then "},{"type":"katex","content":[{"type":"text","text":"n = 10a+b"}],"attrs":{"inline":true}},{"type":"text","text":" for some integer "},{"type":"katex","content":[{"type":"text","text":"a"}],"attrs":{"inline":true}},{"type":"text","text":". After rubbing out the unit digit "},{"type":"katex","content":[{"type":"text","text":"b"}],"attrs":{"inline":true}},{"type":"text","text":" and adding "},{"type":"katex","content":[{"type":"text","text":"5b"}],"attrs":{"inline":true}},{"type":"text","text":" to what remains, we have "},{"type":"katex","content":[{"type":"text","text":"\hat{n} = a+5b"}],"attrs":{"inline":true}},{"type":"text","text":". Since "},{"type":"katex","content":[{"type":"text","text":"\hat{n} + 2n = (a+5b) + 2(10a + b) = 7(3a + b)"}],"attrs":{"inline":true}},{"type":"text","text":", if "},{"type":"katex","content":[{"type":"text","text":"n"}],"attrs":{"inline":true}},{"type":"text","text":" is a multiple of "},{"type":"katex","content":[{"type":"text","text":"7"}],"attrs":{"inline":true}},{"type":"text","text":", then so is "},{"type":"katex","content":[{"type":"text","text":"\hat{n}"}],"attrs":{"inline":true}},{"type":"text","text":". If we start with "},{"type":"katex","content":[{"type":"text","text":"7^{1998}"}],"attrs":{"inline":true}},{"type":"text","text":", any subsequent number obtained by this process must be a multiple of "},{"type":"katex","content":[{"type":"text","text":"7"}],"attrs":{"inline":true}},{"type":"text","text":", and "},{"type":"katex","content":[{"type":"text","text":"1998^7"}],"attrs":{"inline":true}},{"type":"text","text":" is not a multiple of "},{"type":"katex","content":[{"type":"text","text":"7"}],"attrs":{"inline":true}},{"type":"text","text":". Thus we can never end up with "},{"type":"katex","content":[{"type":"text","text":"1998^7"}],"attrs":{"inline":true}},{"type":"text","text":"."}]},{"type":"text","text":"



                            "}]}

