#include <stdbool.h>
#include <stdlib.h>
#include <cs50.h>
#include <math.h>
#include <time.h>
#include <string.h>
#define population 326000000
#define bugs 145173
#define numKnown 500
#define trials 600

// list of element number of every infected person
int subjList[bugs];
// array to be sorted and first few elements added to subjList
int duplicateArray[500];
// keeps track of where the added numbers are
int missingSpots[1000];

void printline(int count) {
   int i;

   for(i = 0;i <count-1;i++) {
      printf("=");
   }

   printf("=\n");
}
//------------------------------------------------------------------------
void display() {
    int i;
    printf("[");
    // navigate through all items 
    for(i = 0;i<50;i++) {
        printf("%d ", subjList[i]);
        if (i%11 == 0) {
            printf("\n");
        }
    }
   printf("]\n");
}

void swap(int num1, int num2) {
   int temp = subjList[num1];
   subjList[num1] = subjList[num2];
   subjList[num2] = temp;
}

int partition(int left, int right, int pivot) {
   int leftPointer = left - 1;
   int rightPointer = right;

   while(true) {
      while(subjList[++leftPointer] < pivot) {
         //do nothing
      }

      while(rightPointer > 0 && subjList[--rightPointer] > pivot) {
         //do nothing
      }

      if(leftPointer >= rightPointer) {
         break;
      } else {
        swap(leftPointer,rightPointer);
      }
   }
   swap(leftPointer,right);
   return leftPointer;
}

void quickSort(int left, int right) {
   if(right-left <= 0) {
      return;   
   } else {
      int pivot = subjList[right];
      int partitionPoint = partition(left, right, pivot);
      quickSort(left,partitionPoint-1);
      quickSort(partitionPoint+1,right);
   }        
}
