      if(leftPointer >= rightPointer) {
         break;
      } else {
        swap2(leftPointer,rightPointer);
      }
   }
   swap2(leftPointer,right);
   return leftPointer;
}

void quickSort2(int left, int right) {
   if(right-left <= 0) {
      return;   
   } else {
