            // swap the place in the array that has these two numbers because they're in the wrong order
            swap(missingSpots[0], missingSpots[0] + 1);
            missingSpots[0] += 1;
            move = 1;
        } else if (subjList[missingSpots[0]] < subjList[missingSpots[0] - 1]) {
            swap(missingSpots[0], missingSpots[0] - 1);
            missingSpots[0] -= 1;
            move = 1;
        }
        if (move == 0) {
